package _05_2For;

public class _240819_Q02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//		2. 
//		*****
//		****
//		***
//		**
//		*
		for(int i = 5; i > 0; i--) {
			for(int j = 0; j < i; j++) {
				System.out.print("*");
			}
			System.out.println();
		}
	}

}
